function time(){var t=new Date,n=t.getHours(),e=t.getMinutes(),o=t.getSeconds(),i=t.getMilliseconds(),c=[n,e,o,i].map(function(t){return 10>t?"0"+t:String(t)});return n=c[0],e=c[1],o=c[2],i=c[3],n+e+o+i}function output(t){var n="#"+t;document.body.bgColor=n,document.body.textContent=n,document.body.style.color="white",document.body.style.fontSize="2em",document.body.style.marginLeft="44%",document.body.style.marginTop="200px"}function startClick(t,n){document.body.addEventListener("dblclick",function(n){t()})}function stopClick(t,n){document.body.addEventListener("click",function(e){t(n)})}function init(){var t=setInterval(function(){output(time())},1e3);stopClick(clearInterval,t),startClick(init)}console.log(time()),init();