function time(){var t=new Date,n=t.getHours(),e=t.getMinutes(),o=t.getSeconds(),i=[n,e,o].map(function(t){return 10>t?"0"+t:String(t)});return n=i[0],e=i[1],o=i[2],n+e+o}function output(t){var n="#"+t;document.body.bgColor=n,document.body.textContent=n,document.body.style.color="white",document.body.style.fontSize="2em",document.body.style.marginLeft="44%",document.body.style.marginTop="200px"}function startClick(t){document.body.addEventListener("dblclick",function(n){t()})}function stopClick(t,n){document.body.addEventListener("click",function(e){t(n)})}function init(){var t=setInterval(function(){output(time())},1e3);stopClick(clearInterval,t),startClick(init)}init();